import Top from "./01_top";
import About from "./02_about";
import Informations from "./03_information";
import Archives from "./04_archive";
import Footer from "./05_footer";
import PropTypes from "prop-types";
import styles from "./home.module.css";

window.addEventListener('scroll', function()
{
  const scrollY = window.scrollY;
  const windowHeight = window.innerHeight;
  const contents = document.querySelectorAll('.homePageContainer > div');
  contents.forEach((content) => {
    const contentTop = content.offsetTop;
    if (scrollY + windowHeight > contentTop) {
      content.classList.add('is-visible');
    }
  });
});

const Home = ({ className = "" }) => {
  return (
    <main className={[styles.subtract, className].join(" ")}>
      <section className={styles.homePageContainer}>
        <div className={styles.homePageContainerChild}>
          <Top />
        </div>
        <div className={styles.homePageContainerChild}>
          <About />
        </div>
        <div className={styles.homePageContainerChild}>
          <Informations />
        </div>
        <div className={styles.homePageContainerChild}>
          <Archives />
        </div>
        <Footer />
      </section>
    </main>
  );
};

Home.propTypes = {
  className: PropTypes.string,
};

export default Home;
