import Top from "./01_top";
import About from "./02_about";
import Informations from "./03_information";
import Archives from "./04_archive";
import Footer from "./05_footer";
import PropTypes from "prop-types";
import styles from "./home.module.css";
import React, { useState, useEffect, useRef } from "react";

const FadeInSection = ({ children }) =>
{
  const [isVisible, setIsVisible] = useState(false);
  const sectionRef = useRef(null);

  useEffect(() =>
  {
    const observer = new IntersectionObserver(
      ([entry]) =>
      {
        if (entry.isIntersecting) {
          setIsVisible(true);
          observer.disconnect(); // 一度発火したら監視を解除
        }
      },
      { threshold: 0.1 } // 10%以上が表示されたらトリガー
    );

    if (sectionRef.current)
    {
      observer.observe(sectionRef.current);
    }

    return () =>
    {
      if (sectionRef.current) observer.unobserve(sectionRef.current);
    };
  }, []);

  return (
    <div
      ref={sectionRef}
      className={`styles.fade-in-section ${isVisible ? "styles.is-visible" : ""}`}
    >
      {children}
    </div>
  );
};

const Home = ({ className = "" }) => {
  return (
    <main className={[styles.subtract, className].join(" ")}>
      <section className={styles.homePageContainer}>
        <FadeInSection>
          <Top />
        </FadeInSection>
        <FadeInSection>
          <About />
        </FadeInSection>
        <FadeInSection>
          <Informations />
        </FadeInSection>
        <FadeInSection>
          <Archives />
        </FadeInSection>
        <Footer />
      </section>
    </main>
  );
};

Home.propTypes = {
  className: PropTypes.string,
};

export default Home;
