import Home from "../home/home";
import styles from "./root.module.css";
import React, { useState } from "react";
import { motion } from "framer-motion";

const Root = () => {
  const [isAnimationComplete, setIsAnimationComplete] = useState(false);

  return (
    <div>
      {/* ローディング画面 */}
      {!isAnimationComplete && (
        <motion.div
          initial={{ opacity: 1 }}
          animate={{ opacity: 0 }}
          transition={{ delay: 3, duration: 1 }} // アニメーション完了後フェードアウト
          onAnimationComplete={() => setIsAnimationComplete(true)}
          className="loading-screen"
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(173, 216, 230, 1)", // 薄い水色
            zIndex: 100,
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            overflow: "hidden",
          }}
        >
          {/* アイコンのアニメーション */}
          <motion.img
            src="/icon.png"
            alt="Icon"
            initial={{ y: "-100%", opacity: 1 }}
            animate={{
              y: "0%",
              scale: [1, 0.7, 1.2],
              opacity: [1, 0, 0],
            }}
            transition={{
              y: { type: "spring", stiffness: 100, damping: 10, duration: 1 },
              scale: { duration: 1, times: [0, 0.7, 1] },
              opacity: { delay: 2, duration: 0.8 },
            }}
            style={{
              width: "150px",
              height: "150px",
            }}
          />
        </motion.div>
      )}

      {/* メインページ */}
      {isAnimationComplete && (
        <motion.div
          initial={{ scale: 0.8, opacity: 0.5 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{ duration: 1.5 }}
          style={{
            backgroundImage: "url('/background.jpg')",
            backgroundSize: "cover",
            backgroundPosition: "center",
            width: "100%",
            height: "100vh",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            color: "#fff",
          }}
        >
          <div className={styles.root}>
      <Home />
    </div>
        </motion.div>
      )}
    </div>
  );
};

export default Root;
