import logo from './logo.svg';
import React, { useState } from 'react';
import './App.css';

function toggleMenu() {
    const nav = document.getElementById('overlay-nav');
    if (nav.classList.contains('show')) {
        nav.classList.remove('show');
        nav.classList.add('hide');
        setTimeout(() => {
            nav.style.display = 'none';
            nav.classList.remove('hide');
        }, 500); // アニメーション時間と同じ
    } else {
        nav.style.display = 'flex';
        setTimeout(() => {
            nav.classList.add('show');
        }, 10); // 少し遅らせてクラスを追加
    }
}

function App()
{
    const [navVisible, setNavVisible] = useState(false);

    return (
        // The rest of the code is the same as the original App.js
        e.jsxs("div", {
            className: "App",
            children: [e.jsxs("header", {
                children: [e.jsx("div", {
                    className: "menu-icon",
                    onClick: toggleMenu,
                    children: "\u9776"
                }), e.jsx("h1", {
                    children: "水瀬いのりセッション会"
                })]
            }), e.jsx("nav", {
                id: "overlay-nav",
                className: navVisible ? 'show' : 'hide',
                children: [e.jsx("div", {
                    className: "close-icon",
                    onClick: toggleMenu,
                    children: "\xD7"
                }), e.jsxs("ul", {
                    children: [e.jsxs("li", {
                        children: [e.jsx("a", {
                            href: "#home",
                            onClick: toggleMenu,
                            children: "\u30DB\u30FC\u30E0"
                        })]
                    }), e.jsxs("li", {
                        children: [e.jsx("a", {
                            href: "#about",
                            onClick: toggleMenu,
                            children: "\u30E1\u30F3\u30D0\u30FC\u7D04\u4ECB"
                        })]
                    }), e.jsxs("li", {
                        children: [e.jsx("a", {
                            href: "#events",
                            onClick: toggleMenu,
                            children: "\u30A4\u30D9\u30F3\u30C8"
                        })]
                    }), e.jsxs("li", {
                        children: [e.jsx("a", {
                            href: "#videos",
                            onClick: toggleMenu,
                            children: "\u52D5\u753B"
                        })]
                    })]
                })]
            }), e.jsxs("main", {
                children: [e.jsx("section", {
                    id: "home",
                    children: e.jsx("h2", {
                        children: "\u30DB\u30FC\u30E0"
                    })
                })]
            }), e.jsxs("footer", {
                children: [e.jsx("p", {
                    children: "\xA9 \u6C34\u702C\u3044\u306E\u308A\u30BB\u30C3\u30B7\u30E7\u30F3\u4F1A"
                })]
            })]
        })
    );
}
export default App;
